import { AxiosRequestConfig, AxiosResponse, AxiosError, Method } from 'axios';


// Define the types for the generic data and payload
type Data<T> = T | null;
interface ApiRequestResult<T> {
  data:  Data<T>;
  error: AxiosError | null;
  isLoading: boolean;
}

interface ApiRequestOptions<T> extends AxiosRequestConfig {
  method?: Method;
  payload?: T;
  queryParams?: { [key: string]: string };
  token?: string; // Accept the token directly as an option
  headers?: { [key: string]: string }; // Custom headers
}

declare function useApiRequestWithAuth<T>(
  url: string,
  options?: ApiRequestOptions<T>
): ApiRequestResult<T>;

export default useApiRequestWithAuth;
